<div id="nav">	<div id="nav_left">		<span class="action-span"><a href="<?php echo site_url('admin') ?>"  target="_top">管理系统</a></span>		<span class="action-span"> » <a href="<?php echo site_url('admin/calendar') ?>" target="main-frame">日程管理</a></span>		<span class="action-span"> » <a href="<?php echo site_url('admin/calendar/month/'.$year.'/'.$month) ?>" target="main-frame"><?php echo $year.'年'.$month.'月' ?></a></span>		 » <?php echo $day.'号' ?>	</div>	<?php if(is_admin() || is_school_admin()): //权限: 只有管理员可以按状态?>	<div id="nav_right">		<select onchange="window.location.href=this.options[this.selectedIndex].value">			<option value='<?php site_url("admin/calendar/day/$year/$month/$day/") ?>'>选择员工</option>			<?php				foreach($staffs as $staff)					echo '<option value="'.site_url("admin/calendar/day/$year/$month/$day/".$staff['staff_id']).'" '.(($staff['staff_id'] == $staff_id)? 'SELECTED' : '').'>'.$staff['name'].'</option>';			?>		</select>	</div>	<?php endif?></div><div style="clear:both"></div><div id="main">	<div id="main_body">		<div class="calendar">			<div class="month-nav">				<a href="<?php echo calendar_url('admin/calendar/day/'.$prev_day, $staff_id, $is_owner) ?>" class="past">上一天</a>				<a href="<?php echo calendar_url('admin/calendar/day/'.date('Y/m/d'), $staff_id, $is_owner) ?>" class="present">回到今天</a>				<a href="<?php echo calendar_url('admin/calendar/day/'.$next_day, $staff_id, $is_owner) ?>" class="future">下一天</a>			</div>		</div>		<div class="title"><?php echo $year.'年'.$month.'月'.$day.'号' ?></div>		<div id="listDiv" class="list-div">			<table cellspacing='1' id="list-table">				<tr>					<th width="300px">时间</th>					<th>内容</th>					<th>操作</th>				</tr>				<?php foreach($one_day_calendar as $event): 					$start_ts = strtotime($event['start_time']);					$end_ts = strtotime($event['end_time']);				?>				<tr>					<td align="center"><?php echo '从'.date('Y年m月d日H:i', $start_ts).'到'.date('Y年m月d日H:i', $end_ts); ?></td>					<td><?php echo nl2br($event['calendar_content']); ?></td>					<td align="center">						<?php if($is_owner): ?>						<a href="<?php echo site_url('admin/calendar/edit/'.$event['calendar_id']) ?>">编辑</a>						<a onclick="return confirm('确定要删除?');" href="<?php echo site_url('admin/calendar/delete/'.$event['calendar_id']) ?>">删除</a>						<?php endif; ?>					</td>				</tr>				<?php endforeach; ?>			</table>		</div>	</div></div>