DROP TABLE IF EXISTS `ndedu_article`;
CREATE TABLE `ndedu_article` (
  `article_id` mediumint(8) unsigned NOT NULL auto_increment,
  `cat_id` smallint(5) NOT NULL default '0',
  `title` varchar(150) NOT NULL,
  `meta_title` varchar(100) default NULL,
  `source` varchar(100) NOT NULL default '尼德教育',
  `author` varchar(50) default NULL,
  `short_description` text,
  `short_description_img` varchar(50) default NULL,
  `content` longtext NOT NULL,
  `keywords` varchar(255) NOT NULL,
  `article_type` tinyint(1) unsigned NOT NULL default '2',
  `is_open` tinyint(1) unsigned NOT NULL default '1',
  `is_deleted` tinyint(1) unsigned NOT NULL,
  `add_time` int(10) unsigned NOT NULL default '0',
  `modified_time` int(10) unsigned NOT NULL,
  `image_url` varchar(255) NOT NULL,
  `image_align` tinyint(21) NOT NULL,
  `count` int(10) unsigned NOT NULL default '0',
  PRIMARY KEY  (`article_id`),
  KEY `title` (`title`),
  KEY `keywords` (`keywords`)
) ENGINE=InnoDB AUTO_INCREMENT=73 DEFAULT CHARSET=utf8 AUTO_INCREMENT=73 ;

