INSERT INTO `ndedu_crm_history_contact` VALUES (1, 1, 3, '1、家长很有意向，已约访周六上午10点～12点。', 0, '2011-01-12 22:30:25', '2011-01-12 22:30:25');
INSERT INTO `ndedu_crm_history_contact` VALUES (2, 4, 3, '1.已和家长联系，家长还是比较认可我们的模式，估计春节后和我们联系.', 0, '2011-01-16 22:00:03', '2011-01-16 22:00:03');
INSERT INTO `ndedu_crm_history_contact` VALUES (3, 7, 3, '1.父母感情不好，孩子对妈妈基本没有记忆，爷爷奶奶照看，有些溺爱，孩子要什么给什么\n2.这周比较忙，没有时间过来，父亲已经将我电话存好，下周过来提前电话我。"													\n', 0, '2011-01-16 22:03:02', '2011-01-16 22:03:02');
INSERT INTO `ndedu_crm_history_contact` VALUES (4, 8, 3, '1.孩子在老家上学，父亲计划让孩子休学回北京暂时学习三个月全托', 0, '2011-01-16 22:04:08', '2011-01-16 22:04:08');
INSERT INTO `ndedu_crm_history_contact` VALUES (5, 9, 3, '1.周日（11.21日）下午三点左右过来，已约好。\n2.班主任家访，这周再联系。"													\n', 0, '2011-01-16 22:05:04', '2011-01-16 22:05:04');
INSERT INTO `ndedu_crm_history_contact` VALUES (6, 11, 3, '1.已约访，周五确定时间。\n', 0, '2011-01-16 22:05:51', '2011-01-16 22:05:51');
INSERT INTO `ndedu_crm_history_contact` VALUES (7, 11, 3, '2.孩子周六上课，周日补课，家长说没时间。感觉家长对孩子问题没有持续性。													\n', 0, '2011-01-16 22:06:13', '2011-01-16 22:06:13');
INSERT INTO `ndedu_crm_history_contact` VALUES (8, 12, 3, '1.已发学校地址信息，周五确定时间。\n', 0, '2011-01-16 22:06:58', '2011-01-16 22:06:58');
INSERT INTO `ndedu_crm_history_contact` VALUES (9, 13, 3, '1.和孩子商量，确定时间后，给我回电话。													\n', 0, '2011-01-16 22:07:23', '2011-01-16 22:07:23');
INSERT INTO `ndedu_crm_history_contact` VALUES (10, 17, 3, '1.明天（12.2）10点电话咨询。', 0, '2011-01-16 22:07:53', '2011-01-16 22:07:53');
INSERT INTO `ndedu_crm_history_contact` VALUES (11, 17, 3, '2.已约访周日，具体时间提前一天确定。家长咨询了我们通过什么方式帮助孩子，和家教有什么区别，尼德教育强调前期咨询、真正找到孩子学习成长问题的原因，然后针对性的以学科辅导和素养辅导结合进行辅导。家长提前一天电话确定具体时间，应该是周日													\n', 0, '2011-01-16 22:08:03', '2011-01-16 22:08:14');
INSERT INTO `ndedu_crm_history_contact` VALUES (12, 18, 3, '1.无法接通。\n2.6点以后再打。													\n', 0, '2011-01-16 22:08:44', '2011-01-16 22:08:44');
INSERT INTO `ndedu_crm_history_contact` VALUES (13, 22, 3, '1.家长安排下时间，周五确定具体约访时间。													', 0, '2011-01-16 22:09:11', '2011-01-16 22:09:11');
INSERT INTO `ndedu_crm_history_contact` VALUES (14, 24, 3, '1.家长似乎没有太多的需求。													', 0, '2011-01-16 22:09:42', '2011-01-16 22:09:42');
INSERT INTO `ndedu_crm_history_contact` VALUES (15, 24, 3, '2.家长主动要求过来，明天（12.8）上午及时电话。\n', 0, '2011-01-16 22:10:14', '2011-01-16 22:10:14');
INSERT INTO `ndedu_crm_history_contact` VALUES (16, 25, 3, '1.下午三点以后联系.\n', 0, '2011-01-16 22:10:47', '2011-01-16 22:10:47');
INSERT INTO `ndedu_crm_history_contact` VALUES (17, 26, 3, '1.访谈回访，家长给孩子报了另外一个记忆力注意力的训练班，游戏、拼图等方式，家长提到孩子似乎对我们的课不感兴趣，因为孩子没有接触到我们的课程内容，所以孩子觉得不喜欢，所以以后针对小学低年级的孩子一定要让素养老师让孩子接触我们的实际内容，让孩子感兴趣。													\n', 0, '2011-01-16 22:11:10', '2011-01-16 22:11:10');
INSERT INTO `ndedu_crm_history_contact` VALUES (18, 27, 3, '1、家长不懂教育，需要把我们的资料让孩子伯伯看看，然后再做决定。			', 0, '2011-01-16 22:11:40', '2011-01-16 22:11:40');
INSERT INTO `ndedu_crm_history_contact` VALUES (19, 27, 3, '2.家长担心孩子形成依赖，其次距离有些远。', 0, '2011-01-16 22:12:09', '2011-01-16 22:12:09');
INSERT INTO `ndedu_crm_history_contact` VALUES (20, 30, 3, '1、回访家长肯定我们的咨询还是比较全面的，的确孩子的学校和很多问题相关，不过由于目前期末考试和作业孩子时间不够，所以可能要寒假，其次家长质疑素养课的效果，也提到路程比较远，再次家长咨询了教师级别是怎么划分的？												', 0, '2011-01-16 22:12:53', '2011-01-16 22:12:53');
INSERT INTO `ndedu_crm_history_contact` VALUES (21, 31, 3, '1.已经联系，家长和孩子商量，家在怀柔\n', 0, '2011-01-16 22:13:24', '2011-01-16 22:13:24');
INSERT INTO `ndedu_crm_history_contact` VALUES (22, 32, 3, '1.家长有些忙，已发信息，有空联系我们。\n', 0, '2011-01-16 22:13:52', '2011-01-16 22:13:52');
INSERT INTO `ndedu_crm_history_contact` VALUES (23, 19, 3, '1.已咨询，孩子好动，不爱上学，但比较聪明。家长很无奈，希望让其过来上课，但是距离比较远，在大兴。', 0, '2011-01-16 22:15:49', '2011-01-16 22:15:49');
INSERT INTO `ndedu_crm_history_contact` VALUES (24, 20, 3, '1.孩子相对比较听话，一直在家长管理下学习，自主和独立思考能力预计较差，家长给孩子安排了不少课程，计划抽时间过来。\n', 0, '2011-01-16 22:17:07', '2011-01-16 22:17:07');
INSERT INTO `ndedu_crm_history_contact` VALUES (25, 21, 3, '1.已约访下周六（12.11）上午10点(家长提出取消)\n', 0, '2011-01-16 22:17:30', '2011-01-16 22:17:30');
INSERT INTO `ndedu_crm_history_contact` VALUES (26, 23, 3, '"1.已约访周日上午10点。\n2.访谈后回访，家长认可我们的咨询，只是觉得费用比较高，我提了学费减免的情况。"		', 0, '2011-01-16 22:18:08', '2011-01-16 22:18:08');
INSERT INTO `ndedu_crm_history_contact` VALUES (27, 14, 3, '1.周日（11.28）上午10点过来，需要再联系下。\n', 0, '2011-01-16 22:19:32', '2011-01-16 22:19:32');
INSERT INTO `ndedu_crm_history_contact` VALUES (28, 33, 4, '和朱睿妈妈联系，确定2月19号下午三点的咨询时间，但是家长说有事来不了，取消预约时间。', 0, '2011-02-18 16:45:46', '2011-02-18 16:45:46');
