INSERT INTO `ndedu_crm_student` VALUES (5, '杭盖', 302, '', '15148930720', 0, 'wzwl8216630@163.com', '你们好，了解到你们的工作特征，觉得太有价值了，我是一位老师，我一直有一个愿望，等退休了办一个类似于你们的教育辅助机构，帮助那些家长，可是我连自己的儿子都没弄明白，很惭愧，而你们的成就给我一丝希望，我想帮助孩子的同时，想向你们学习，打开我们这里的市场，不知道，能不能给学习机会，我是内蒙古，兴安盟的。\n', 1, 19, 269, 2257, '内蒙兴安盟乌兰浩特', 1, 3, 0, 0, '2011-01-13 11:06:08', '2011-01-13 11:06:08');
INSERT INTO `ndedu_crm_student` VALUES (6, '崔韵霞', 302, '', '13718605374', 0, 'cuiyunxia13747397@126.com', '以联系，家长对首次收费比较敏感', 1, 2, 52, 506, '北京丰台', 1, 3, 0, 0, '2011-01-13 11:21:29', '2011-01-13 11:21:29');
INSERT INTO `ndedu_crm_student` VALUES (7, '张xx', 100, '张先生: 13911889197', '', 0, '', '任性不听话 不上幼儿园 隔代教育', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:34:50', '2011-01-13 11:34:50');
INSERT INTO `ndedu_crm_student` VALUES (8, '王xx', 401, '王先生: 18601338982', '', 0, '', '成绩不错，但开始一些坏习惯，抽烟、喝酒、打架\n孩子在老家上学，父亲计划让孩子休学回北京暂时学习三个月全托', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:36:10', '2011-01-13 11:36:10');
INSERT INTO `ndedu_crm_student` VALUES (9, '梁xx', 203, '', '梁女士: 13071129656', 0, '', '语文知识不扎实，书写不工整、潦草、难看；作业拖拉、慢', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:37:17', '2011-01-13 11:37:17');
INSERT INTO `ndedu_crm_student` VALUES (10, 'xx', 203, '', '罗女士: 13436385733', 0, '', '学习一般，家长辅导，但成绩上不去', 1, 2, 52, 501, '西城区阜成门附近住', 1, 3, 0, 0, '2011-01-13 11:38:46', '2011-01-13 11:38:46');
INSERT INTO `ndedu_crm_student` VALUES (11, '狄越', 303, '狄先生: 13911269119', '吴女士: 13520658238', 0, '', '逆反，烦家长谈学习，小四换了班主任，孩子就开始不愿意和家长沟通学习', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:40:04', '2011-01-13 11:40:04');
INSERT INTO `ndedu_crm_student` VALUES (12, 'xx', 202, '', '京女士: 13521328732', 0, '', '数学英语成绩还可以，但学校不是太好的学校，妈妈认为孩子学习方法存在问题，需要辅导.', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:41:20', '2011-01-13 11:41:20');
INSERT INTO `ndedu_crm_student` VALUES (13, '周策', 301, '', '崔秀芳: 13693077200', 0, '', '英语97、/10，政治95/120，语文比较差. 不喜欢学校管理, 因为管理太严. 孩子是男孩, 容易烦躁. 小初衔接的问题比较大.\n', 1, 2, 52, 511, '北京市顺义区牛栏山第一中学', 1, 3, 0, 0, '2011-01-13 11:43:38', '2011-01-13 11:43:38');
INSERT INTO `ndedu_crm_student` VALUES (14, '李易宣', 201, '', '13911064341', 0, '', '贪玩、作业拖拉', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:45:09', '2011-01-13 11:45:09');
INSERT INTO `ndedu_crm_student` VALUES (15, 'xx', 201, '', '孙xx: 13391653678', 0, '', '1. 自理能力弱 2. 做事慢 3. 不见成绩提高', 1, 2, 52, 505, '北京市宣武区右安门内大街宣师一附小', 1, 3, 0, 0, '2011-01-13 11:47:06', '2011-01-13 11:47:06');
INSERT INTO `ndedu_crm_student` VALUES (16, '齐峰', 100, '齐先生: 15101174785', '', 0, '', '幼小衔接\n', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:48:22', '2011-01-13 11:48:22');
INSERT INTO `ndedu_crm_student` VALUES (17, '朱航', 202, '朱先生: 13120053221', '', 0, '', '家长对孩子学习、特长兴趣没有头绪，不知道该如何指导孩子发展；其次孩子不喜欢英语、不愿意在家写作业', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:49:29', '2011-01-13 11:49:29');
INSERT INTO `ndedu_crm_student` VALUES (18, '宇昕', 301, 'xxx: 13910391435', '', 0, '', '我的孩子小学英语基础没打好，现在上初一英语学习很吃力这可怎么办？', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:50:32', '2011-01-13 11:50:32');
INSERT INTO `ndedu_crm_student` VALUES (19, '牛世勋', 302, '', '李女士: 13121178297', 0, '', '学习没有兴趣，上课爱说话，家长教师反映孩子很聪明，觉得有些浪费。', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:51:29', '2011-01-13 11:51:29');
INSERT INTO `ndedu_crm_student` VALUES (20, '贾维洲', 301, '13651070246', '', 0, '', '1.孩子相对比较听话，一直在家长管理下学习，自主和独立思考能力预计较差，家长给孩子安排了不少课程，计划抽时间过来。', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:52:20', '2011-01-13 11:52:20');
INSERT INTO `ndedu_crm_student` VALUES (21, '许靖德', 100, '', '王女士: 13910972898', 0, '', '情绪不好，特别是别人打搅其工作', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:55:59', '2011-01-13 11:55:59');
INSERT INTO `ndedu_crm_student` VALUES (22, '管梅雪', 205, '', '13611369592', 0, '', '自尊心强，性格内向，在老家上学，爷爷奶奶溺爱。 老师伤了孩子自尊心，孩子不上学了。', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:56:47', '2011-01-13 11:56:47');
INSERT INTO `ndedu_crm_student` VALUES (23, '金鑫', 205, '', '13120219931', 0, '', '学习一直不主动，目前已出现逆反。家长比较着急。', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 11:58:02', '2011-01-13 11:58:02');
INSERT INTO `ndedu_crm_student` VALUES (24, '朱禹西', 203, '', '13071129656', 0, '', '男孩，9岁，四年级。语文不好。学习习惯不好。上课走神，写作业拖拉', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 13:05:00', '2011-01-13 13:05:00');
INSERT INTO `ndedu_crm_student` VALUES (25, '姜xx', 204, '姜xx: 51836981', '', 0, '', '成绩一般, 主要是小升初. 现在在中古友谊小学.', 1, 2, 52, 501, '北京市西城区中古友谊小学', 1, 3, 0, 0, '2011-01-13 13:05:47', '2011-01-13 13:05:47');
INSERT INTO `ndedu_crm_student` VALUES (26, '冉曙光', 200, '', '李女士: 13717850887', 0, '', '妈妈想辅导记忆力', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 13:08:46', '2011-01-13 13:08:46');
INSERT INTO `ndedu_crm_student` VALUES (27, '罗治文', 200, '', '18710198286', 0, '', '孩子不爱思考', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 13:10:12', '2011-01-13 13:10:12');
INSERT INTO `ndedu_crm_student` VALUES (28, '郭春平', 202, '', '13520411518', 0, '', '平时父母认真辅导孩子作业，可是考试就掉链子，50多分，父母很伤心', 1, 2, 52, 502, '无', 1, 3, 0, 0, '2011-01-13 13:13:04', '2011-01-13 13:13:04');
