INSERT INTO `ndedu_crm_student` VALUES (31, 'xx', 303, '', '杨丽梅: 13716603589', 0, '', '初一初二很好，初三由于老师关系成绩直线下降. 已经联系，家长和孩子商量，家在怀柔.', 1, 2, 52, 513, '北京市怀柔区', 1, 3, 0, 0, '2011-01-13 13:18:13', '2011-01-13 13:18:13');
