<!-- center 图片 --><div class="index_center">	<img src="images/children/article_center_image.jpg"></div><div class="article_centent">	<div class="left">		<div class="centent_title">			<div class="bg_warp"><div><?php echo (isset($single)) ? utf_substr($article_info['title'], 18) : utf_substr($article_info['cat_name'], 18); ?>			</div></div>		</div>		<div class="content">			<?php if(isset($left_bar)): ?>			<ul id="article_ledt_bar">			<?php				$i = 0;				foreach($left_bar as $val)				{					if(isset($val['url']))						echo '<li onmouseover="switch_tag(\'article_ledt_bar\', '.$i.')">'.'<a href="'.site_url($val['url']).'">'.utf_substr($val['text'], 21).'</a>';					else						echo '<li>'.$val['text'];										echo '</li>';					$i++;				}			?>			</ul>			<?php else: ?>			<img src="images/children/left_bar.jpg">			<?php endif; ?>		</div>	</div>	<div class="right">		<div class="nav">			当前位置：<a href="<?php echo site_url('children'); ?>">首页</a> > <?php echo (isset($single)) ? '' : $article_info['cat_name'].' > ' ?> <?php echo $article_info['title'] ?>		</div>		<div class="content">			<?php echo $article_info['content'] ?>		</div>	</div>	<div class="clearfix"></div></div>